import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsxRuntime classic */
/* @jsx mdx */
import SEO from '../components/SEO'


const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <SEO path='/toolset' title='Toolset' mdxType="SEO" />
    <h1>{`Work and Experiments`}</h1>
    <p>{`This is a list of all the tools I'm actively using, have used before, or have learned but haven't had the chance to use in a real world project yet. I currently have a thing for TypeScript üòÄ.`}</p>
    <h4>{`JavaScript Utilities`}</h4>
    <p>{`Lodash, Underscore, and jQuery. Don't use them anymore, the language has grown enough.`}</p>
    <h4>{`CSS Frameworks/Libraries`}</h4>
    <p>{`Skeleton, Semantic UI, Bootstrap 4, Foundation, and Materialize. Haven't really used them much.`}</p>
    <h4>{`Templating Engines`}</h4>
    <p>{`Pug ‚ù§Ô∏è, Slim, and Handlebars. Mainly for small projects.`}</p>
    <h4>{`Preprocessors`}</h4>
    <p>{`Sass, SCSS, Stylus, and PostCSS. I don't know where to put CSSModules but I use it all the time so ü§∑‚Äç‚ôÇ.`}</p>
    <h4>{`UI Frameworks and State Management`}</h4>
    <p>{`React ‚ù§Ô∏è, Redux, and messed around with Vue just a tad bit.`}</p>
    <h4>{`CSS-in-JS`}</h4>
    <p>{`Glamorous/Emotion, and Styled Components. Not a fan of CSS-in-JS
though.üôÖ`}</p>
    <h4>{`Build Tools and Transpilers`}</h4>
    <p>{`Babel, Webpack ‚ù§Ô∏è, Gulp.`}</p>
    <h4>{`Databases and Cache`}</h4>
    <p>{`MongoDB ‚ù§Ô∏è, Firebase Firestore, Redis.`}</p>
    <h4>{`Server-Side and Serverless Technologies`}</h4>
    <p>{`Mongoose, NodeJS, Express, GraphQL, Firebase Functions, and SocketIO.`}</p>
    <h4>{`Static Site Generators and CMS`}</h4>
    <p>{`GatsbyJS, NetlifyCMS`}</p>
    <h4>{`Deployment, and CI/CD`}</h4>
    <p>{`GatsbyJS, Heroku, Netlify ‚ù§Ô∏è, Firebase, Git/GitHub, and TravisCI.`}</p>
    <h4>{`Testing`}</h4>
    <p>{`Jest ‚ù§Ô∏è, Enzyme, and Cypress.`}</p>
    <h4>{`Design and UI/UX Tools`}</h4>
    <p>{`Adobe Illustrator, Adobe Photoshop, Adobe XD, Figma ‚ù§Ô∏è, and Zeplin.`}</p>
    <h1>{`Development Environment`}</h1>
    <p>{`This is my development environment setup. Tools I use to be productive,
all around.`}</p>
    <h4>{`Editor + Terminal üë®‚Äçüíª`}</h4>
    <p><a parentName="p" {...{
        "href": "https://code.visualstudio.com/"
      }}>{`Visual Studio Code`}</a>{` is my current editor. It allows a lot of customisation. I'm using `}<a parentName="p" {...{
        "href": "https://marketplace.visualstudio.com/items?itemName=ahmadawais.shades-of-purple"
      }}>{`Shades of Purple`}</a>{` as my main theme and sometimes switch to `}<a parentName="p" {...{
        "href": "https://marketplace.visualstudio.com/items?itemName=Hyzeta.vscode-theme-github-light"
      }}>{`GitHub Light`}</a></p>
    <p>{`.
My main fonts are Space Mono and FiraCode, currently using the former.`}</p>
    <p>{`Firefox is my main, and I use Chrome for testing alongside Firefox.`}</p>
    <p><a parentName="p" {...{
        "href": "https://cmder.net/"
      }}>{`Cmder`}</a>{` is my terminal of choice with
custom theme and
`}<a parentName="p" {...{
        "href": "https://github.com/AmrEldib/cmder-powerline-prompt"
      }}>{`integrations`}</a>{`.`}</p>
    <h4>{`Productivity üí™`}</h4>
    <p><a parentName="p" {...{
        "href": "https://www.notion.so/"
      }}>{`Notion`}</a>{` is my main editorial app. Blog
post drafts, to-do, and channel-related stuff.
In terms of project management my tool of choice is
`}<a parentName="p" {...{
        "href": "https://trello.com/"
      }}>{`Trello`}</a>{`.
As for time-tracking, I use a tool that I built myself called
`}<a parentName="p" {...{
        "href": "https://auditmyday.netlify.app/"
      }}>{`AuditMyDay`}</a>{`.
Anything else? Probably have a bash/node script for it. ####
Recording + Editing üìπ`}</p>
    <p>{`I mainly use OBS to record any and all videos, and use Vegas Pro 17 as my
editor.`}</p>
    <p>{`Photoshop is used to make thumbnails as well as Figma.`}</p>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
